import back from "@/components/Botton/back.vue";
// import { apiGetMissionList, apiAddMission, apiReceiveMissionReward } from "@/api/mission.js";
import { apiGetMatchs } from "@/api/match";
import { apiClubUpload, apiMemApplyClub } from "@/api/Club";
export default {
  components: {
    back
  },
  data() {
    return {
      noMission: false,
      checkList: [],
      matchsList: [],
      busy: false,
      loading: false,
      push: true,
      IndexNum: 1,
      noMore: false,
      clubName: '',
      clubSlogan: '',
      upImg: false
    };
  },

  created() {
    // this.getTaskList(this.categoryType);
  },
  mounted() {
      //   let data1 = {
      //   matchTypeId: 2,
      //   index: 1,
      //   count: 10,
      //   havaPlay: true,
      //   matchItemTypeId: -1
      // };
      // apiGetMatchs(data1, true).then(res => {
      //   console.log(res)
      //   this.matchsList = res.Data;
      // });
  },
  methods: {
    creat () {
      if(this.clubName == ""){
        this.$message({
          message: this.$t("FillYourClub"),
          type: "error",
          center: true,
          duration: 1200
        });
      }else if(this.clubSlogan == ""){
        this.$message({
          message: this.$t("FillYourClubSlogan"),
          type: "error",
          center: true,
          duration: 1200
        });
      }else if(this.checkList.toString() == ""){
        this.$message({
          message: this.$t("FillYourFavoriteEvent"),
          type: "error",
          center: true,
          duration: 1200
        });
      }else if(localStorage.getItem("imgName") == ""){
        this.$message({
          message: this.$t("UploadYourClubLogo"),
          type: "error",
          center: true,
          duration: 1200
        });
      }else {
        let memApplyClubData = {
          ClubName: this.clubName,
          SloganLabel: this.clubSlogan,
          MatchId: this.checkList.toString()+",",
          ClubImg: localStorage.getItem("imgName"),
          UName: localStorage.getItem("uname")
        }
        apiMemApplyClub(memApplyClubData, true).then(res =>{
          if(res.Success){
            this.$message({
              message: res.Message,
              type: "success",
              center: true,
              duration: 1200
            });
            this.$router.push("/Myself");
          }else{
            this.$message({
              message: res.Message,
              type: "error",
              center: true,
              duration: 1200
            });
          }
        })
      }
    },
    onLoad() {
      this.busy = true;
       let data1 = {
        matchTypeId: 2,
        index: this.IndexNum,
        count: 8,
        havaPlay: true,
        matchItemTypeId: -1
      }
       apiGetMatchs(data1, true).then(res => {
        if(res.Data.length == 0){
          this.noMore = true
          this.push = false;
          return
        }
        for(let i = 0; res.Data.length>i; i++){
          this.matchsList.push(res.Data[i])
          this.busy = false;
        }
        this.IndexNum++
       })
    },
    // 图片上传功能
    tirggerFile (event) {
      let file = event.target.files[0]
      let imgSize = file.size
      var param = new FormData() // 创建form对象
      param.append('file', file, file.name) // 通过append向form对象添加数据
      param.append('type', '1') // 添加form表单中其他数据
      // console.log(param.get('file')) // FormData私有类对象，访问不到，可以通过get判断值是否传进去

      if (!event.target.files.length) return
      if (imgSize / 1024 > 10000) {
        this.$message({
          message: this.$t("PictureIsTooBig"),
          type: "warning",
          center: true,
          duration: 1200
        });
      } else {
        this.imgPreview(file)
      }
    },

    // 获取图片
    imgPreview (file, callback) {
      let _this = this // this指向问题，所以在外面先定义
      let config = {
        headers: { 'Content-Type': 'multipart/form-data' },
        contentType: false, //required
        processData: false // required
      }
      // 判断是否支持FileReader
      if (!file || !window.FileReader) return
      // 创建一个reader
      var reader = new FileReader()
      // 将图片转成base64格式
      reader.readAsDataURL(file)
      reader.onloadend = function () {
        let result = this.result
        let img = new Image()
        img.src = result
        // console.log('============未压缩图片===========')
        img.onload = function () {
          let data = _this.compress(img)

          _this.imgUrl = result

          let blob = _this.dataURItoBlob(data)

          // console.log('*******base64转blob对象******')
          let param = new FormData() // 创建form对象
          param.append('file', blob, file.name) // 通过append向form对象添加数据
          param.append('type', "club")

          apiClubUpload(param, config).then(res => {
            if (res.Success) {
              _this.$message({
                message: _this.$t("UploadSuccessfully"),
                type: "success",
                center: true,
                duration: 1200
              });
              //顯示圖片
              _this.upImg = true
              document.querySelector('#demo').setAttribute('src', result);
              localStorage.setItem("imgName", file.name)
            }else {
              _this.$message({
                message: _this.$t("UploadFailed"),
                type: "error",
                center: true,
                duration: 1200
              });
            }
          })
        }
      }
    },
    // 压缩图片
    compress (img) {
      let canvas = document.createElement('canvas')
      let ctx = canvas.getContext('2d')
      // let initSize = img.src.length
      let width = img.width
      let height = img.height
      canvas.width = width
      canvas.height = height
      // 铺底色
      ctx.fillStyle = '#fff'
      ctx.fillRect(0, 0, canvas.width, canvas.height)
      ctx.drawImage(img, 0, 0, width, height)

      // 进行最小压缩
      let ndata = canvas.toDataURL('image/jpeg', 0.1)
      // console.log('*******压缩后的图片大小*******')
      return ndata
    },
    dataURItoBlob (base64Data) {
      var byteString
      if (base64Data.split(',')[0].indexOf('base64') >= 0) byteString = atob(base64Data.split(',')[1])
      else byteString = unescape(base64Data.split(',')[1])
      var mimeString = base64Data
        .split(',')[0]
        .split(':')[1]
        .split(';')[0]
      var ia = new Uint8Array(byteString.length)
      for (var i = 0; i < byteString.length; i++) {
        ia[i] = byteString.charCodeAt(i)
      }
      return new Blob([ia], { type: mimeString })
    }
  },
  destroyed() {
    localStorage.setItem("imgName","")
  }
};
