import back from "../../components/Botton/back.vue";
import {
  apiGetChallengeMatchList,
  apiGetChallengeMatchItemList
} from "@/api/match";
export default {
  components: {
    back
  },
  created() {
    this.getmatch();
  },
  data() {
    return {
      activeNames: ["1"],
      match: "",
      matchitem: "",
      mouth: this.$route.params.id,
      id: "",
      loading: true
    };
  },
  methods: {
    handleChange(val) {
      this.id = val;

      let id1 = {
        challengeId: this.id
      };
      apiGetChallengeMatchItemList(id1, true).then(res => {
        this.matchitem = res;
      });
    },
    getmatch() {
      let data = {
        month: this.$route.params.id
      };
      apiGetChallengeMatchList(data, true).then(res => {
        this.match = res;
        this.id = res.Data[0].ChallengeId;
        let id1 = {
          challengeId: this.id
        };
        apiGetChallengeMatchItemList(id1, true).then(res => {
          this.matchitem = res;
        });
      });
      // let id1 = {
      //   challengeId: this.$store.state.match.ChallengeId
      // };
    }
  }
};
