import inputComp from "@/components/Index/Input.vue";
import { apiLogin } from "@/api/User";
export default {
  components: {
    inputComp
  },
  computed: {
    ifPassword() {
      return this.togglePassword ? "password" : "text";
    }
  },
  created() {
    let token = localStorage.getItem("token");
    if (token) {
      this.$router.push("/home");
    }
  },
  data() {
    return {
      account: "",
      password: "",
      imgCode: "",
      togglePassword: true,
    };
  },
  methods: {
    gofp() {
      this.$router.push("/ForgetPassword");
    },
    gorg() {
      this.$router.push("/register");
    },
    getAccount(res) {
      this.account = res;
    },
    getPassword(res) {
      this.password = res;
    },
    // login () {
    //   let data = {
    //     Name: this.account,
    //     Password: this.password
    //   };
    //   this.$store.dispatch("asyncLogin", data);
    //   // let result = this.$store.state.user.code;
    //   // if (result === 200) {
    //   //   this.$toast.success("登录成功");
    //   //   setTimeout(() => {
    //   //     this.$router.push("/home");
    //   //   });
    //   // }
    // }
    login(v) {
      let data = {
        Name: this.account,
        Password: this.password
      };

      apiLogin(data).then(res => {
        if (res.code === 200) {
          localStorage.setItem("token", res.AccessToken);
          localStorage.setItem("UId", res.UId);
          localStorage.setItem("RefreshToken", res.RefreshToken);
          localStorage.setItem("logintime", new Date().getTime());
          localStorage.setItem("uname", this.account);
          const m = this.$message({
              message: "登录成功",
              duration: 1000,
              type: "success",
              center: true
          });
          this.$router.push("/home");
        } else if (res.Code === 600) {
          const m = this.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
          setTimeout(() => m.close(), 1200);
        }
      });
    }
  },
  mounted() {
    this.account = localStorage.getItem("uname");
  }
};
