import NavBottom from "@/components/Main/NavBottom.vue";
import back from "../components/Botton/back.vue";
import model from "../components/model";
import FlowAd from "@/components/FlowAd";
import { apiGetFlowAdByType} from "@/api/FlowAd.js";
import {
  apiGetBetResponse,
  apiBet,
  apiGetMatchItemAndMainPlay
} from "../api/match.js";
import {betSuccess,betPass} from "@/utils/medias";

export default {
  watch: {
    //获得钱
    betnum: function() {
      if (
        this.betinfo.PlayType == 3 ||
        this.betinfo.PlayType == 4 ||
        this.betinfo.PlayType == 5 ||
        this.betinfo.PlayType == 6
      ) {
        this.money = (this.betnum * this.betinfo.OddsValue).toFixed(2);
      } else {
        this.money = (
          this.betnum * this.betinfo.OddsValue -
          this.betnum
        ).toFixed(2);
      }
      this.rangeValue = parseInt(this.betnum);
    },
  },
  components: {
    NavBottom,
    back,
    model,
    FlowAd
  },

  data() {
    return {
      titlenum: "",

      tab1: false,
      tab2: false,
      tab3: false,

      show: false,
      showbet: false,
      betnum: 0,
      rangeValue: 0,
      rangeMaxVal: 0,
      money: "",
      playtype: 0,
      playId: "",
      betinfo: "",
      num: 0,
      fastnum: "",
      loading: false,
      isrunTwice: false,
      IsOddsChange: false,
      lastOdds: "",
      newOdds: "",
      timer: {
        timer1: "",
        timer2: "",
        timer3: ""
      },
      countDownTimer: "",
      countDownTime: 30,
      isOpenAd: false,
      adTypeId: 0,
      isDisable: false,
      teamMatchItem: [],
    };
  },
  created() {
    // this.gettitlenum();
    this.$store.commit("upMatchitems", "")
    this.titlenum = localStorage.getItem("titlenum")
    this.initGetMatchPlay();
    // await this.default();
  },
  methods: {
    fastbet(id) {
      this.fastnum = id;
      this.betnum = id;
    },
    getTime(time) {
      if (time == "") {
        return "0";
      }

      let timeNum = Number(time);
      return timeNum < 45
        ? this.$t("FirstHalf") + timeNum + "'"  // 【 上半场 】
        : timeNum == 45
        ? this.$t("Halftime")  // 【 半场 】
        : this.$t("SecondHalf") + (timeNum - 45) + "";   // 【 下半场 】
    },

    gojump(id) {
      this.$router.push("/matchmore");
    },
    //关闭下注
    closedialog() {
      this.showbet = false;
    },

    //下注界面
    showBetFn(id) {
      this.betnum = 0;

      this.playId = id;
      if (this.playId == -1) {
        return;
      }
      let data = {
        playId: id,
        betType: 1
      };
      apiGetBetResponse(data, true).then(res => {
        if (res.Code === 600) {
          this.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
        } else {
          this.showbet = true;

          this.betinfo = res;
          // 上次的舊賠率
          this.lastOdds = this.betinfo.OddsValue;
          // 投注金額預設最低投注金額
          this.betnum = res.MinBetIntegral;

          this.rangeValue = this.betnum;

          // 判斷最大單筆投注金額回傳是否為-1 如是拉桿的最大值為10000
          if (res.MaxBetIntegral === -1) this.rangeMaxVal = 10000;
          else this.rangeMaxVal = res.MaxBetIntegral;

          // 判斷玩家餘額大於最大可投注額時，滾動條最大值設為最大可投注額
          if (this.betinfo.AvailableIntegral > this.rangeMaxVal) {
            this.rangeMaxVal = 10000;
            this.isDisable = false;
            return;
          }
          // 判斷玩家餘額小於最小可投注額時，指针放置在开始位置显示最小可投注额 且不可拖动
          if (this.betinfo.AvailableIntegral < this.betinfo.MinBetIntegral) {
            this.isDisable = true;
            return;
          }
          // 判斷玩家餘額小於最大可投注額時，滾動條最大值設為玩家餘額
          if (this.betinfo.AvailableIntegral < this.rangeMaxVal) {
            this.rangeMaxVal = this.betinfo.AvailableIntegral;
            this.isDisable = false;
            return;
          }
        }
      });
    },
    changeRangeVal() {
      this.betnum = this.rangeValue;

      let tooltip = document.querySelector(".slide-tooltip");
      let rangeBtn = document.querySelector(".el-slider__bar");

      let tooltipWidth = tooltip.getBoundingClientRect().width; // 提示框最正確的寬度
      let rangeBtnOffset = rangeBtn.offsetWidth; // 拖動拉桿時拉桿條的偏移量
      let rangeBtnOffsetParent = rangeBtn.offsetParent.offsetLeft; // 拉桿條離父層左邊(父層padding-left)的偏移量

      let tolerance = tooltipWidth / 2 + (rangeBtnOffsetParent - tooltipWidth); // 公差
      if (!this.isrunTwice) {
        tooltip.style.left = tolerance + "px";
        this.isrunTwice = true;
      } else {
        tooltip.style.left = rangeBtnOffset + tolerance + "px";
      }
    },
    //投注金额
    addbet() {
      this.betnum = this.betnum + 10;
    },
    losebet() {
      if (this.betnum == 0) {
        return;
      }
      this.betnum = this.betnum - 10;
    },
    //可赢金额
    getmoney1() {
      // this.money = this.betnum * this.betinfo.OddsValue;
      // this.money = this.money.toFixed(2);
    },
    // 點擊下注時判斷
    betClick() {
      this.newChangeOdds().then(() => {
        // 如果兩個賠率不同
        if (this.lastOdds !== this.newOdds) {
          this.showbet = false;
          this.IsOddsChange = true;
          this.getMatchPlay();
        } else {
          this.bet();
        }
      });
    },

    // 若前後賠率相同則執行投注
    async bet() {
      let hthis = this;
      let data = {
        playId: this.playId,
        betIntegral: this.betnum,
        betType: 1
      };

      if (this.betnum >= 10) {
        await apiBet(data, true).then(res => {
          if (res.IsBetSucceed) {
            this.$message({
              message: this.$t("BettingSuccess"),  // 【 下注成功 】
              duration: 2500,
              type: "success",
              center: true
            });
            this.fastnum = "";
            this.showbet = false;
            // 下注成功音效
            betSuccess.play();
            // 彈出廣告
            this.adTypeId = 2;
            let data = {
              flowAdTypeId: this.adTypeId
              };
            apiGetFlowAdByType(data, true).then(res => {
              if(Object.keys(res.Data).length > 0) {
                this.isOpenAd = true;
              } else {
                this.isOpenAd = false;
              }
            });
          } else if (res.Code == 600) {
            this.$message({
              message: res.Message,
              duration: 3000,
              type: "error",
              center: true
            });
            if (res.Message == "余额不足") {
              // 彈出廣告
              setTimeout(() => {
                this.isOpenAd = true;
                this.adTypeId = 2;
              }, 1000);
              this.showbet = false;
            }
          }
        });
      } else {
        this.$message.error(this.$t("BetErrorMessage"));  // 【 投注金额需要大于10元 】
      }
      // 刷新API
      this.getMatchPlay();
    },
    async default() {      
      // 如果外面是點選[全部]進來的 
      if (this.titlenum == -1) {
          // 沒有早盤的話跑今日
          if (this.$store.state.match.Matchitems.length == 0) {
            this.tabshow(1)
            .then(() => {
              // 沒有今日的話跑滾球
              if (this.$store.state.match.Matchitems.length == 0) {
                this.tabshow(2)
              }
            });
          }
      }
      // 如果外面是點選[早盤]進來的 
      else if (this.titlenum == 0) {
        this.tabshow(0)
      }
      // 如果外面是點選[今日]進來的 
      else if (this.titlenum == 1) {
        this.tabshow(1)
      }
      // 如果外面是點選[滾球]進來的 
      else if (this.titlenum == 2) {
        this.tabshow(2)
      }
      this.cycling();
    },


    //畫面一進來時候获取早盤玩法
    async initGetMatchPlay() {
      this.tabshow(0).then(() => {
        this.default()
      });
    },

    //获取赛事玩法
    async getMatchPlay() {
      this.$store.dispatch("asyncGetMatchplay", {
        id: this.playtype
      });
    },
    async tabshow(id) {
      this.$store.commit("uploading", true);
      this.loading = true;
      this.playtype = id;
      if (id == 0) {
        this.tab1 = true;
        this.tab2 = false;
        this.tab3 = false;
      }
      if (id == 1) {
        this.tab1 = false;
        this.tab2 = true;
        this.tab3 = false;
      }
      if (id == 2) {
        this.tab1 = false;
        this.tab2 = false;
        this.tab3 = true;
      }
       await this.$store.dispatch("asyncGetMatchplay", {
        id: id
      });
      this.cycling();
    },

    cycling() {
      // 早盤
      if (this.tab1) {
        // 停止全部計時器
        this.destroyed();
        // 再開啟計時器
        this.timer.timer1 = setInterval(() => {
          this.getMatchPlay();
        }, 30000);
        this.countDownTime = 30;
        this.countDown();
      }

      // 今日
      if (this.tab2) {
        // 停止全部計時器
        this.destroyed();
        // 再開啟計時器
        this.timer.timer2 = setInterval(() => {
          this.getMatchPlay();
        }, 30000);
        this.countDownTime = 30;
        this.countDown();
      }

      // 滾球
      if (this.tab3) {
        // 停止全部計時器
        this.destroyed();
        // 再開啟計時器
        this.timer.timer3 = setInterval(() => {
          this.getMatchPlay();
        }, 2000);
      }
    },
    // 倒數計時
    countDown() {
      this.countDownTimer = setInterval(() => {
        if (this.countDownTime <= 0) {
          this.countDownTime = 30;
        }
        this.countDownTime = this.countDownTime - 1;
      }, 1000);
    },
    // 手動刷新
    refresh() {
      this.getMatchPlay();
      this.cycling();

      let vm = this;
        const m = vm.$message({
          message: this.$t("RefreshedSuccessfully"),
          duration: 3000,
          type: "success",
          center: true
        });
        setTimeout(() => {m.close()}, 2000);
    },
    // 停止全部計時器
    destroyed() {
      clearInterval(this.timer.timer1);
      clearInterval(this.timer.timer2);
      clearInterval(this.timer.timer3);
      clearInterval(this.countDownTimer);
    },
    gettitlenum() {
      if (this.$store.state.match.titlenum == 2) {
        this.tabshow(2);
        this.$store.commit("uptitlenum", {
          id: -1
        });
        return;
      }
      if (this.$store.state.match.titlenum == 0) {
        this.tabshow(0);
        this.$store.commit("uptitlenum", {
          id: -1
        });
        return;
      }
    },

    // 新賠率
    async newChangeOdds() {
      if (this.playId == -1) {
        return;
      }
      let data = {
        playId: this.playId,
        betType: 1
      };
      await apiGetBetResponse(data, true).then(res => {
        if (res.Code === 600) {
          this.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
        } else {
          this.betinfo = res;
          this.newOdds = this.betinfo.OddsValue;
        }
      });
    },

    closeOddsChange() {
      this.getMatchPlay();
      this.showbet = false;
      this.IsOddsChange = false;
    },

    // 俱樂部投注
    goClubBet(item) {
      // 判斷有無獨贏玩法
      if (item.CanJoinGambling == true) {
        localStorage.setItem("matchItemId", item.MatchItemId);
        setTimeout(() => {
          this.$router.push("InitiateActivities");
        }, 250)
      } else {
        let vm = this;
        const m = vm.$message({
          message: "无独赢赔率，请选择其他比赛。",
          duration: 3000,
          type: "error",
          center: true
        });
        setTimeout(() => m.close(), 2000);
      }
    }
  },

  // 跳出本頁時 vue hook(生命週期)
  destroyed() {
    clearInterval(this.timer.timer1);
    clearInterval(this.timer.timer2);
    clearInterval(this.timer.timer3);
    clearInterval(this.countDownTimer);
    // this.$store.commit("upMatchitems", "")
  }
};
