import back from "@/components/Botton/back.vue";
import {
  apiGetClubGamblingBetResultList,
  apiGetClubGamblingBetResult
} from "@/api/ClubGamblings.js";

export default {
  components: {
    back
  },
  data() {
    return {
      lang: "",
      resultData: [],
      betId: "",
      isLoading: false,
      isNoData: false,
    };
  },
  created() {
    this.lang = JSON.parse(localStorage.getItem("lang")).tag;
    if ( localStorage.getItem("singleResult") == undefined || localStorage.getItem("singleResult") == null || localStorage.getItem("singleResult") == "false") {
      this.getBetResult();
    } else {
      this.betId = localStorage.getItem("betId");
      this.singleBetResult();
    }
  },
  methods: {
    getBetResult() {
      // 所有賽果列表()
      this.isLoading = true;
      apiGetClubGamblingBetResultList("", true).then(res => {
        if(Math.abs(res.Code) >= 600) {
          let vm = this;
          const m = vm.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
          setTimeout(() => {
            m.close();
            this.$router.push("/myClub");
          }, 2000);
          return;
        } else if (res.Data.length == 0) {
          this.isLoading = false;
          this.isNoData = true;
        } else {
          this.isLoading = false;
          this.isNoData = false
          this.resultData = res.Data;
        }
      });
    },

    // 單一點擊查看
    singleBetResult() {
      let data = {
        clubGamblingId: this.betId
      };
      this.isLoading = true;
      apiGetClubGamblingBetResult(data, true).then(res => {
        if(Math.abs(res.Code) >= 600) {
          let vm = this;
          const m = vm.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
          setTimeout(() => {
            m.close();
            this.$router.push("/myClub");
          }, 2000);
          return;
        }else if (res.Data.length == 0) {
          this.isLoading = false;
          this.isNoData = true;
        } else {
          this.isLoading = false;
          this.isNoData = false
          this.resultData = res.Data;
        }
      });
    }
  },
  destroyed() {
    localStorage.setItem("singleResult", false);
  }
};
