import back from "@/components/Botton/back.vue";
import { apiGetClubMemLog } from "@/api/Club.js";
export default {
  components: {
    back,
  },
  data() {
    return {
      categoryIndex: 0,
      top10List: [
        {
          UImg: "",
          UName: "",
          BetSumNum: 0,
          Contribution: 0
        }
      ],

      allList: [
        {
          UImg: "",
          UName: "",
          BetSumNum: 0,
          Contribution: 0
        }
      ],

      // v-infinite-scroll 套件
      busy: false,
      immediate: false,
      indexNum: 1,
      totalPages: 0,
    };
  },
  created() {
    this.getlist();
  },
  methods: {
    // 獲取會員俱樂部資訊
    getlist() {
      let data = {
        index: this.indexNum,
        count : 10
      }
      apiGetClubMemLog(data, true).then(res => {
        this.top10List = res.Top5Data;
        this.allList = res.Data.Data;
        this.totalPages = res.Data.TotalPages;
      });
    },

    // 所有成員無限加載
    load() {
      console.log('dasd')
      if (this.indexNum >= this.totalPages) {
        this.busy = true;
        return;
      }
      this.indexNum++;
      let data = {
        index: this.indexNum,
        count : 10
      }
      apiGetClubMemLog(data, true).then(res => {
        res.Data.Data.forEach(el => {
          this.allList.push(el);
        });
      });
    }
  }
};
