import back from "@/components/Botton/back.vue";
import { apiGetClubAnnouncement, apiAddOrUpClubAnnouncement} from "@/api/Club.js";
export default {
  components: {
    back,
  },
  data() {
      return {
          inputTitle: "",
          inputTextarea: "",
          id: 0,
          textLen: 0,
          maxTextLen: 200,
          doubleClick: false,
      }
  },
  created() {
      this.getNoticeList();
  },
  methods: {
    // 獲取公告列表
    getNoticeList() {
      let data = {
        ndex: this.indexNum,
        count: 10
      };
      apiGetClubAnnouncement(data, true).then(res => {
        this.inputTitle = res.Data.Data[0].Title;
        this.inputTextarea = res.Data.Data[0].Content;
        this.id = res.Data.Data[0].Id;
      }).then(() => {
        this.textLen = this.inputTextarea.length;
      });
    },

    // 字數檢測
    textcontrol() {
      let len = this.inputTextarea.length;
      this.textLen = len;
    },
    // 編輯公告
    addOrEditNotice() {
      if (this.textLen > this.maxTextLen) {
        this.$message({
          message: this.$t('MoreEditableWords'),
          type: "warning",
          center: true,
          duration: 1200
        });
        return;
      }
      if (this.doubleClick == false) {
        let data = {
          Title: this.inputTitle,
          Content: this.inputTextarea,
          Status: 1,
          Id: this.id
        }
        apiAddOrUpClubAnnouncement(data, true).then(res => {
          if (res.Success) {
            let vm = this;
            const m = vm.$message({
                message: res.Message,
                duration: 3000,
                type: "success",
                center: true
            });
            setTimeout(() => {
                m.close();
                this.$router.push("/myClub");
            }, 2000);
          } else {
              let vm = this;
            const m = vm.$message({
                message: res.Message,
                duration: 3000,
                type: "warning",
                center: true
            });
            setTimeout(() => m.close(), 2000);
          }
        });
      }
      this.doubleClick = true
    },
  },
}
