import inputComp from "@/components/Index/Input.vue";
import { apiRegister, apiGetCaptchCode } from "@/api/User.js";
export default {
  components: {
    inputComp
  },
  created() {
    this.getParams();
  },
  computed: {
    ifPassword() {
      return this.togglePassword ? "password" : "text";
    }
  },
  data() {
    return {
      togglePassword: true,
      account: "",
      password: "",
      imgCode: "",
      phone: "",
      confirmPassword: "",
      showcode: true,
      InvitationCode: "",
      url: process.env.VUE_APP_API_BASE_URL + "/User/GetCaptchCode",
      GetCaptchCodeIndex: 0,
      isCheck: true,
    };
  },
  methods: {
    getParams() {
      let params = this.$route.query.InviterId;
      this.InvitationCode = params;
    },
    gojump() {
      this.$router.push("/");
    },
    //验证码
    recode() {
      this.url = process.env.VUE_APP_API_BASE_URL + "/User/GetCaptchCode?"+this.GetCaptchCodeIndex;
      this.GetCaptchCodeIndex++;
      new Date().getTime();
    },
    sendRegister() {
      let data = {
        Name: this.account,
        Password: this.password,
        ConfirmPassword: this.confirmPassword,
        PhoneNumber: this.phone,
        ImgCode: this.imgCode,
        InvitationCode: this.InvitationCode
      };

      if (this.account == "") {
        const m = this.$message({
          message: this.$t("AccountEmpty"),
          duration: 1000,
          type: "error",
          center: true
        });
        setTimeout(() => m.close(), 1200);
        return;
      };
      if (this.password == "") {
        const m = this.$message({
          message: this.$t("PasswordBlank"),
          duration: 1000,
          type: "error",
          center: true
        });
        setTimeout(() => m.close(), 1200);
        return;
      };
      if (this.phone == "") {
        const m = this.$message({
          message: this.$t("PhoneBlank"),
          duration: 1000,
          type: "error",
          center: true
        });
        setTimeout(() => m.close(), 1200);
        return;
      };
      if (this.imgCode == "") {
        const m = this.$message({
          message: this.$t("VerificationCodeEmpty"),
          duration: 1000,
          type: "error",
          center: true
        });
        setTimeout(() => m.close(), 1200);
        return;
      };
      if (!this.isCheck) {
        const m = this.$message({
          message: this.$t("AgreePrivacy"),
          duration: 1000,
          type: "error",
          center: true
        });
        setTimeout(() => m.close(), 1200);
        return;
      }
      apiRegister(data).then(res => {
        if (res.AccessToken) {
          const m = this.$message({
            message: this.$t('RegisteredSuccessfully'),
            duration: 3000,
            center: true,
            type: "success"
          });
          setTimeout(() => m.close(), 1200);
          setTimeout(() => {
            this.$router.push("/Login");
          }, 3000);
        } else if (Math.abs(res.Code) >= 600) {
          const m = this.$message({
            message: res.Message,
            duration: 3000,
            center: true,
            type: "error"
          });
          setTimeout(() => m.close(), 1200);
        }
      });
    },

    getAccount(res) {
      this.account = res;
    },
    GetPassword(res) {
      this.password = res;
    },
    getConfirmPassword(res) {
      this.confirmPassword = res;
    },
    getPhone(res) {
      this.phone = res;
    },
    getImgCode(res) {
      this.imgCode = res;
    }
  }
};
